import 'dart:async';
import 'dart:io';
import 'package:bloc/bloc.dart';
import 'package:cloud_firestore/cloud_firestore.dart';
import 'package:equatable/equatable.dart';
import 'package:firebase_storage/firebase_storage.dart' as firebase_storage;
import 'package:flower/configs/app_config.dart';
import 'package:flower/models/note.dart';
import 'package:flower/models/plant_model.dart';
import 'package:flower/models/reminder.dart';
import 'package:flutter/foundation.dart';
import 'package:fluttertoast/fluttertoast.dart';
import 'package:meta/meta.dart';
part 'firestore_bloc_event.dart';
part 'firestore_bloc_state.dart';

class FirestoreBlocBloc extends Bloc<FirestoreBlocEvent, FirestoreBlocState> {
  FirestoreBlocBloc() : super(FirestoreBlocState.empty());
  FirebaseFirestore _db = FirebaseFirestore.instance;

  @override
  Stream<FirestoreBlocState> mapEventToState(
    FirestoreBlocEvent event,
  ) async* {
    if (event is SavePlant) {
      try {
        await _db
            .collection('users')
            .doc(AppConfig().deviceId)
            .collection('plant_saved')
            .doc(event.id)
            .set(event.body);
      } catch (e) {
        print(e);
      }
    }
    if (event is DeletePlant) {
      try {
        yield state.copyWith(
          deletePlantLoading: true,
        );
        await _db
            .collection('users')
            .doc(AppConfig().deviceId)
            .collection('plant_saved')
            .doc(event.plantId)
            .delete();
        yield state.copyWith(
          deletePlantSuccess: true,
        );
      } catch (e) {
        print(e);
      }
      yield state.copyWith(
        deletePlantSuccess: false,
        deletePlantLoading: false,
      );
    }

    if (event is ChangeFavourite) {
      try {
        yield state.copyWith(removeFavouriteLoading: true);
        var data = await _db
            .collection('users')
            .doc(AppConfig().deviceId)
            .collection('plant_saved')
            .doc(event.plantId)
            .get();
        if (data.exists) {
          await _db
              .collection('users')
              .doc(AppConfig().deviceId)
              .collection('plant_saved')
              .doc(event.plantId)
              .delete();
        } else {
          add(SavePlant(id: event.plantId, body: event.body));
        }

        yield state.copyWith(
          removeFavouriteLoading: false,
        );
      } catch (e) {
        Fluttertoast.showToast(msg: e.toString());
        yield state.copyWith(
          removeFavouriteError: e.toString(),
          removeFavouriteLoading: false,
        );
      }
      yield state.copyWith(
        removeFavouriteError: '',
        removeFavouriteLoading: false,
      );
    }
    if (event is SaveToRecentSnap) {
      try {
        await _db
            .collection('users')
            .doc(AppConfig().deviceId)
            .collection('recent_snaps')
            .doc(event.id)
            .set(event.body);
      } catch (e) {
        print(e);
      }
    }
    if (event is SaveNote) {
      try {
        yield state.copyWith(
          saveNoteLoading: true,
        );
        List<String> images = [];
        if (event.images.isNotEmpty) {
          for (int i = 0; i < event.images.length; i++) {
            String imageUrl = await uploadImageToFirebase(
                event.images[i], 'users/${AppConfig().deviceId}/notes/images');
            images.add(imageUrl);
          }
        }
        await _db
            .collection('users')
            .doc(AppConfig().deviceId)
            .collection('plant_saved')
            .doc(event.plantId)
            .collection('notes')
            .add(event.body..addAll({'images': images}));

        yield state.copyWith(
          saveNoteLoading: false,
          saveNoteSuccess: true,
        );
      } catch (e) {
        yield state.copyWith(
            saveNoteError: e.toString(), saveNoteLoading: false);
      }
      yield state.copyWith(
        saveNoteError: '',
        saveNoteLoading: false,
        saveNoteSuccess: false,
      );
    }
    if (event is UpdateNote) {
      try {
        yield state.copyWith(
          saveNoteLoading: true,
        );
        List<String> images = [];
        if (event.images.isNotEmpty) {
          for (int i = 0; i < event.images.length; i++) {
            String imageUrl = await uploadImageToFirebase(
                event.images[i], 'users/${AppConfig().deviceId}/notes/images');
            images.add(imageUrl);
          }
        }

        await _db
            .collection('users')
            .doc(AppConfig().deviceId)
            .collection('plant_saved')
            .doc(event.plantId)
            .collection('notes')
            .doc(event.noteId)
            .set(event.body..addAll({'images': event.imagesUploaded + images}));
        yield state.copyWith(
          saveNoteLoading: false,
          saveNoteSuccess: true,
        );
      } catch (e) {
        yield state.copyWith(
            saveNoteError: e.toString(), saveNoteLoading: false);
      }
      yield state.copyWith(
        saveNoteError: '',
        saveNoteLoading: false,
        saveNoteSuccess: false,
      );
    }
    if (event is DeleteNote) {
      try {
        yield state.copyWith(
          saveNoteLoading: true,
        );
        await _db
            .collection('users')
            .doc(AppConfig().deviceId)
            .collection('plant_saved')
            .doc(event.plantId)
            .collection('notes')
            .doc(event.noteId)
            .delete();
        yield state.copyWith(
          saveNoteLoading: false,
          deleteNoteSuccess: true,
        );
      } catch (e) {
        yield state.copyWith(
            saveNoteError: e.toString(), saveNoteLoading: false);
      }
      yield state.copyWith(
        saveNoteError: '',
        saveNoteLoading: false,
        deleteNoteSuccess: false,
      );
    }

    if (event is SaveReminder) {
      try {
        yield state.copyWith(
          saveReminderLoading: true,
        );
        await _db
            .collection('users')
            .doc(AppConfig().deviceId)
            .collection('plant_saved')
            .doc(event.plantId)
            .collection('reminders')
            .doc(event.body['type'].toString())
            .set(event.body);
        yield state.copyWith(
          saveReminderLoading: false,
          saveReminderSuccess: true,
        );
      } catch (e) {
        yield state.copyWith(
            saveReminderError: e.toString(), saveReminderLoading: false);
      }
      yield state.copyWith(
        saveReminderLoading: false,
        saveReminderError: '',
        saveReminderSuccess: false,
      );
    }

    ///RECENT SNAP
    if (event is AddToRecentSnap) {
      try {
        await _db
            .collection('users')
            .doc(AppConfig().deviceId)
            .collection('recent_snaps')
            .doc(event.plantId)
            .set(event.body);
      } catch (e) {
        print(e);
      }
    }
    if (event is MoveRecentSnapToMyPlant) {
      try {
        await _db
            .collection('users')
            .doc(AppConfig().deviceId)
            .collection('recent_snaps')
            .doc(event.plantId)
            .delete();
        add(SavePlant(id: event.plantId, body: event.body));
      } catch (e) {
        Fluttertoast.showToast(msg: e);
      }
    }

    ///List Reminders
    if (event is GetListReminders) {
      try {
        var data = await _db
            .collection('users')
            .doc(AppConfig().deviceId)
            .collection('plant_saved')
            .get();
        var plants =
            data.docs.map((e) => PlantModel.fromJson(e.data())).toList();
        print(plants);
        List<Reminder> list = [];
        for (int i = 0; i < plants.length; i++) {
          var element = plants[i];
          var reminders = await _db
              .collection('users')
              .doc(AppConfig().deviceId)
              .collection('plant_saved')
              .doc(element.id.toString())
              .collection('reminders')
              .get();
          var results = reminders.docs
              .map((e) =>
                  Reminder.fromJson(e.data()).copyWith(plantName: element.name))
              .toList();
          results =
              results.map((e) => e.copyWith(plantName: element.name)).toList();
          list.addAll(results);
        }

        yield state.copyWith(listReminders: list);
      } catch (e) {
        Fluttertoast.showToast(msg: e.toString());
      }
    }
  }

  Stream<List<PlantModel>> getListMyPlantStream() {
    return _db
        .collection('users')
        .doc(AppConfig().deviceId)
        .collection('plant_saved')
        .snapshots()
        .asyncMap((event) {
      return event.docs.map((e) {
        return PlantModel.fromJson(e.data());
      }).toList();
    });
  }

  Stream<List<Note>> getListNotes(String plantId) {
    return _db
        .collection('users')
        .doc(AppConfig().deviceId)
        .collection('plant_saved')
        .doc(plantId)
        .collection('notes')
        .orderBy('date', descending: true)
        .snapshots()
        .asyncMap((event) => event.docs
            .map((e) => Note.fromJson(e.data()..addAll({'id': e.id})))
            .toList());
  }

  Stream<List<Reminder>> getListReminders(String plantId) {
    return _db
        .collection('users')
        .doc(AppConfig().deviceId)
        .collection('plant_saved')
        .doc(plantId)
        .collection('reminders')
        .orderBy('type', descending: true)
        .snapshots()
        .asyncMap((event) =>
            event.docs.map((e) => Reminder.fromJson(e.data())).toList());
  }

  Stream<bool> isFavourite(String plantId) {
    return _db
        .collection('users')
        .doc(AppConfig().deviceId)
        .collection('plant_saved')
        .doc(plantId)
        .snapshots()
        .asyncMap((event) {
      return event.exists;
    });
  }

  Stream<List<PlantModel>> getListRecentSnap() {
    return _db
        .collection('users')
        .doc(AppConfig().deviceId)
        .collection('recent_snaps')
        .snapshots()
        .asyncMap((event) {
      return event.docs.map((e) {
        return PlantModel.fromJson(e.data());
      }).toList();
    });
  }

  Stream<List<PlantModel>> getListPlantModel({List<String> ids}) {
    return _db.collection('plants').snapshots().asyncMap((event) {
      if (ids.isNotEmpty) {
        return event.docs.where((element) => ids.contains(element.id)).map((e) {
          return PlantModel.fromJson(e.data());
        }).toList();
      }
      return event.docs.map((e) {
        return PlantModel.fromJson(e.data());
      }).toList();
    });
  }

  Future createData() async {
    mockData.forEach((key, value) async {
      await _db.collection('plants').doc(key).set(value);
    });
  }

  Future<String> uploadImageToFirebase(File file, String path) async {
    String fileName = file.path.split('/').last;
    var reference = firebase_storage.FirebaseStorage.instance
        .ref()
        .child('$path/$fileName');
    firebase_storage.UploadTask task = reference.putFile(file);
    task.whenComplete(() => null);
    await task.whenComplete(() {
      return null;
    });
    return reference.getDownloadURL();
  }
}

